import React, { useState, useEffect } from 'react';
import {
  Container,
  Row,
  Col,
  ToggleButton,
  ToggleButtonGroup,
  Card,
  Button,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Price per kg as a range
const pricePerKg = {
  '토너': { min: 30000, max: 40000 }, // Assumed price for Toner
  '세럼': { min: 50000, max: 60000 },
  '앰플': { min: 50000, max: 60000 }, // Same as Serum
  '크림': { min: 60000, max: 70000 },
};

const App = () => {
  const [product, setProduct] = useState(null);
  const [volume, setVolume] = useState(null);
  const [quantity, setQuantity] = useState(null);
  const [estimate, setEstimate] = useState({ min: 0, max: 0 });
  const [unitPrice, setUnitPrice] = useState({ min: 0, max: 0 });
  const [view, setView] = useState('form'); // 'form' or 'result'

  const productOptions = ['토너', '세럼', '앰플', '크림'];
  const volumeOptions = ['50ml', '100ml', '150ml'];
  const quantityOptions = [1000, 1500, 3000, 5000];

  // Reset quantity if user selects 50ml and 1000 was already selected
  useEffect(() => {
    if (volume === '50ml' && quantity === 1000) {
      setQuantity(null);
    }
  }, [volume]);

  const handleCalculate = () => {
    if (product && volume && quantity) {
      const basePriceRange = pricePerKg[product];

      let discount = 1.0;
      if (quantity === 3000) {
        discount = 0.9; // 10% discount on price/kg
      } else if (quantity === 5000) {
        discount = 0.8; // 20% discount on price/kg
      }

      const discountedPricePerKg = {
          min: basePriceRange.min * discount,
          max: basePriceRange.max * discount,
      };

      const weightInGrams = parseInt(volume.replace('ml', ''));
      const weightInKg = weightInGrams / 1000;

      const minUnitPrice = discountedPricePerKg.min * weightInKg;
      const maxUnitPrice = discountedPricePerKg.max * weightInKg;

      const minEstimate = minUnitPrice * quantity;
      const maxEstimate = maxUnitPrice * quantity;

      setUnitPrice({ min: minUnitPrice, max: maxUnitPrice });
      setEstimate({ min: minEstimate, max: maxEstimate });
      setView('result');
    }
  };

  const handleReset = () => {
    setProduct(null);
    setVolume(null);
    setQuantity(null);
    setUnitPrice({ min: 0, max: 0 });
    setEstimate({ min: 0, max: 0 });
    setView('form');
  };

  const formatPrice = (price) => {
      return price > 0 ? `${Math.round(price).toLocaleString()}원` : '';
  }

  const renderForm = () => (
    <>
      {/* Option Selection */}
      <Row className="mb-3">
        <Col>
          <h5>제품군</h5>
          <ToggleButtonGroup type="radio" name="product-options" value={product} onChange={setProduct}>
            {productOptions.map((p) => (
              <ToggleButton key={p} id={`product-${p}`} value={p} variant="outline-primary">
                {p}
              </ToggleButton>
            ))}
          </ToggleButtonGroup>
        </Col>
      </Row>

      <Row className="mb-3">
        <Col>
          <h5>용량</h5>
          <ToggleButtonGroup type="radio" name="volume-options" value={volume} onChange={setVolume}>
            {volumeOptions.map((v) => (
              <ToggleButton key={v} id={`volume-${v}`} value={v} variant="outline-primary">
                {v}
              </ToggleButton>
            ))}
          </ToggleButtonGroup>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col>
          <h5>수량</h5>
          {volume === '50ml' && (
            <p style={{ fontSize: '0.9rem', color: '#f0f0f0', marginTop: '-5px', marginBottom: '10px' }}>
              50ml는 1500개 이상부터 생산 가능합니다.
            </p>
          )}
          <ToggleButtonGroup type="radio" name="quantity-options" value={quantity} onChange={setQuantity}>
            {quantityOptions.map((q) => (
              <ToggleButton 
                key={q} 
                id={`quantity-${q}`} 
                value={q} 
                variant="outline-primary"
                disabled={volume === '50ml' && q === 1000}
              >
                {q}개
              </ToggleButton>
            ))}
          </ToggleButtonGroup>
        </Col>
      </Row>

      <Row>
        <Col className="d-grid">
          <Button 
            size="lg" 
            onClick={handleCalculate} 
            disabled={!product || !volume || !quantity}
          >
            견적 보기
          </Button>
        </Col>
      </Row>
    </>
  );

  const renderResult = () => (
    <>
        <div className="text-center">
            <img src="/logo.png" alt="Factosquare Logo" className="logo-image" />
            <p style={{fontSize: '1.2rem', margin: '0'}}>
              개당 예상 생산가
            </p>
            <h3 className="mb-4">{formatPrice(unitPrice.min)} ~ {formatPrice(unitPrice.max)}</h3>

            <p style={{fontSize: '1.2rem', margin: '0'}}>
              총 예상 생산가
            </p>
            <h2>{formatPrice(estimate.min)} ~ {formatPrice(estimate.max)}</h2>
        </div>
        <Row className="mt-4">
            <Col className="d-grid">
                <Button size="lg" onClick={handleReset} variant="secondary">
                    다시 계산하기
                </Button>
            </Col>
        </Row>
    </>
  );

  return (
    <Container className="mt-5">
      <Card className="glass-card">
        {view === 'form' && (
            <Card.Header as="h2" className="text-center">1분 만에<br />견적 확인하기</Card.Header>
        )}
        <Card.Body>
          {view === 'form' ? renderForm() : renderResult()}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default App;
