import React, { useState, useEffect } from 'react';
import {
  Container,
  Row,
  Col,
  Button,
  ButtonGroup,
  Card,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// In-memory DB for pricing
const pricingData = {
  '세럼/앰플': {
    '50ml': { min: 2500, max: 3000 },
    '100ml': { min: 4500, max: 5500 }, // Assumed price
    '150ml': { min: 6000, max: 7500 }, // Assumed price
  },
  '크림': {
    '50ml': { min: 3500, max: 4000 },   // Assumed price
    '100ml': { min: 6500, max: 7500 },  // Assumed price
    '150ml': { min: 9000, max: 10500 }, // Assumed price
  },
};


const App = () => {
  const [product, setProduct] = useState(null);
  const [volume, setVolume] = useState(null);
  const [quantity, setQuantity] = useState(null);
  const [estimate, setEstimate] = useState({ min: 0, max: 0 });

  const productOptions = ['세럼/앰플', '크림'];
  const volumeOptions = ['50ml', '100ml', '150ml'];
  const quantityOptions = [1000, 3000, 5000];

  useEffect(() => {
    if (product && volume && quantity) {
      const priceInfo = pricingData[product]?.[volume];
      if (priceInfo) {
        const minTotal = priceInfo.min * quantity;
        const maxTotal = priceInfo.max * quantity;
        setEstimate({ min: minTotal, max: maxTotal });
      } else {
        setEstimate({ min: 0, max: 0 });
      }
    } else {
      setEstimate({ min: 0, max: 0 });
    }
  }, [product, volume, quantity]);

  const formatPrice = (price) => {
      return price > 0 ? `${price.toLocaleString()}원` : '';
  }

  return (
    <Container className="mt-5">
      <Card className="glass-card">
        <Card.Header as="h2">화장품 생산 견적</Card.Header>
        <Card.Body>
          <Row className="mb-3">
            <Col>
              <h5>제품군</h5>
              <ButtonGroup>
                {productOptions.map((p) => (
                  <Button
                    key={p}
                    variant={product === p ? 'primary' : 'outline-primary'}
                    onClick={() => setProduct(p)}
                  >
                    {p}
                  </Button>
                ))}
              </ButtonGroup>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col>
              <h5>용량</h5>
              <ButtonGroup>
                {volumeOptions.map((v) => (
                  <Button
                    key={v}
                    variant={volume === v ? 'primary' : 'outline-primary'}
                    onClick={() => setVolume(v)}
                  >
                    {v}
                  </Button>
                ))}
              </ButtonGroup>
            </Col>
          </Row>

          <Row className="mb-4">
            <Col>
              <h5>수량</h5>
              <ButtonGroup>
                {quantityOptions.map((q) => (
                  <Button
                    key={q}
                    variant={quantity === q ? 'primary' : 'outline-primary'}
                    onClick={() => setQuantity(q)}
                  >
                    {q}개
                  </Button>
                ))}
              </ButtonGroup>
            </Col>
          </Row>

          <Row>
            <Col>
              <h4>
                예상 견적: {estimate.min > 0 ? 
                  `${formatPrice(estimate.min)} ~ ${formatPrice(estimate.max)}` :
                  '옵션을 모두 선택해주세요.'
                }
              </h4>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default App;
